<?php&#10&#10namespace Drupal\custom\Plugin\Sandwich;&#10&#10use Drupal\custom\SandwichBase;&#10&#10/**&#10 * Provides a ham sandwich.&#10 *&#10 * @Sandwich(&#10 *   id = "ham_sandwich",&#10 *   description = @Translation("Ham, mustard, rocket, sun-dried tomatoes."),&#10 *   calories = 426&#10 * )&#10 */&#10class ExampleHamSandwich extends SandwichBase {&#10&#10  /**&#10   * {inheritdoc}&#10   */&#10  public function order(array $extras) {&#10    $ingredients = array('ham, mustard', 'rocket', 'sun-dried tomatoes');&#10    $sandwich = array_merge($ingredients, $extras);&#10    return 'You ordered an ' . implode(', ', $sandwich) . ' sandwich. Enjoy!';&#10  }&#10&#10}&#10